import random 

names = nm1 + nm2 + nm3 + nm4 + nm5 

def makename(length):

    madenames = []

    for i in range(length):

        name = random.choice(names)
        name = name[0].upper() + name[1:].lower()
        madenames.append(name)

    return ' '.join(madenames)

degree = ["Lawful", "Neutral", "Chaotic"]
alignment = ["Good", "Neutral", "Evil"]

def makealignment():

    made = []

    random.shuffle(degree)
    random.shuffle(alignment)

    made.append(degree[0])
    made.append(alignment[0])

    if made == ["Neutral", "Neutral"]:

        made = ["True", "Neutral"]

    return ' '.join(made) 

