def makepeople(amount):

    madepeople = []

    for i in range(amount):

        madepeople.append(
            f"""| {makename(random.randint(1, 4))} | {random.choice(["M", "F"])} | {random.choice(races)} | {random.choices(classes)[0]} | {random.choice(Politics)} | {makealignment()} | {maketraits()} |""")

    return madepeople

def makepages(amount):

    pages = []

    for i in range(amount):

        totalpage = (
    """<div class='classTable wide'>\n""" + 
    """##### NPC Table \n""" + 
    """| Name | Sex | Race | Class | Allegiance | Alignment | Appearance |\n""" + 
    """|:----:|:---:|:----:|:-----:|:----------:|:---------:|:----------:|\n""" + 
    '\n'.join(makepeople(27)) +'\n</div>'
    )

        pages.append(totalpage)

    return '\n\page\n'.join(pages)
